import React, {Component} from 'react'

class App extends Component {
  state = {
    data: [],
  }

  // Code is invoked after the component is mounted/inserted into the DOM tree.
  componentDidMount() {
    const url =
      'https://reqres.in/api/users/'

    fetch(url)
      .then((result) => result.json())
      .then((result) => {
        this.setState({
          data: result,
        })
      })
  }

  render() {
    return (
      <div>
        <h2>User Data...</h2>
        <table>
          <thead>
            <tr>
              <th>Id</th>
              <th>Email</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Avatar</th>
            </tr>
          </thead>
          <tbody>
          </tbody>
        </table>
      </div>
      );

      {this.state.data.data.map(user => (
            <tr key={user.Id}>
              <td>{user.id}</td>
              <td>{user.email}</td>
              <td>{user.first_name}</td>
              <td>{user.last_name}</td>
              <td>{user.avatar}</td>
              </tr>
        ))}


    //return <div><pre>{JSON.stringify(users.data, null, 2) }</pre></div>;
  }
}

export default App
